/**
 * # Al crear el archivo de css, lo debemos mandar a llamar dentro de nuestro js del componente
 * # para ingresar las clases se declaran como className
 * # los props, seran los parametros que recibira la funcion de nuestro componente. Estos han sido mandados desde la funcion app
 */
import './ExpenseItem.css';

function ExpenseItem(props) {
  /*const espenseDate = new Date(2022, 2, 28);
  const expenseTitle = 'Lavado del auto';
  const expensePrice = 140.00;*/

  /**
   * # toLocalString,devuelve un cadena con la representaci칩n al idioma de la fecha especificada, se ingresa la localidad y la opcion
   * # getFullYear, devuelve el a침o de la fecha indicada acorde a la hora local.
   */

  const month = props.date.toLocaleString('es-MX', { month: 'long' });
  const day = props.date.toLocaleString('es-MX', { day: '2-digit' });
  const year = props.date.getFullYear();

  return (
    <div className="expense-item">
      <div>
        <div>{month}</div>
        <div>{year}</div>
        <div>{day}</div>
      </div>
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className="expense-item__price">${props.amount}</div>
      </div>
    </div>
  );
}

export default ExpenseItem;
