import React, { useState, useEffect, useReducer, useContext } from 'react';

import Card from '../UI/Card/Card';
import classes from './Login.module.css';
import Button from '../UI/Button/Button';
import AuthContext from '../../store/auth-context';

// * funcion para validar el correo ingresado
const emailReducer = (state, action) => {
  switch (action.type) {
    case 'USER_INPUT': {
      return {
        value: action.val,
        isValid: action.val.includes('@')
      }
    }
    case 'INPUT_BLUR': {
      return {
        value: state.value,
        isValid: state.value.includes('@')
      }
    }
    default: {
      return {
        value: '',
        isValid: false
      }
    }
  }
};

const passwordReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_PASSWORD': {
      return {
        value: action.val,
        isvalid: action.val.trim().length > 6
      }
    }
    case 'INPUT_BLUR': {
      return {
        value: state.value,
        isValid: state.value.trim().length > 6
      }
    }
    default: {
      return {
        value: '',
        isValid: false
      }
    }
  }
};

const Login = (props) => {
  /*  const [enteredEmail, setEnteredEmail] = useState('');
   const [emailIsValid, setEmailIsValid] = useState(); */
  /* const [enteredPassword, setEnteredPassword] = useState('');
  const [passwordIsValid, setPasswordIsValid] = useState(); */
  const authctx = useContext(AuthContext);;
  const [formIsValid, setFormIsValid] = useState(false);

  // * manejo de los datos ingresado por useReducer
  const [emailState, dispatchEmail] = useReducer(emailReducer, {
    value: '',
    isValid: null
  });

  const [passwordState, dispatchPassword] = useReducer(passwordReducer, {
    value: '',
    isValid: null
  });

  // * Obtenemos los valores del objeto solo cuando isValid cambia
  const { isValid: emailIsValid } = emailState;
  const { isValid: passwordIsValid } = passwordState;

  useEffect(() => {

    const identifier = setTimeout(() => {
      console.log('se creo el efecto secundario');
      setFormIsValid(
        emailIsValid && passwordIsValid
      );
    }, 500);

    /*
    * Esta seccion nos permite que no se realizen varias peticiones cada vez que el usuario teecle algo
    * permitiendo reiniciar el temporizador y ejecutarlo solo hasta que haya pasado determinado tiempo
    */
    return () => {
      console.log('Se ha borrado el efecto secundario');
      clearTimeout(identifier);
    }

  }, [emailIsValid, passwordIsValid]);

  // * obtenemos los datos del usuario
  const emailChangeHandler = (event) => {
    dispatchEmail({
      type: 'USER_INPUT',
      val: event.target.value
    });

    /*  setFormIsValid(
       event.target.value.includes('@') && passwordState.isValid
     ); */
  };

  const passwordChangeHandler = (event) => {
    dispatchPassword({
      type: 'ADD_PASSWORD',
      val: event.target.value
    });

    /* setFormIsValid(
      emailState.isValid && event.target.value.trim().length > 6
    ); */
  };

  // * Si no ingreso un campo o es incorrecto lo pintara de rojo

  const validateEmailHandler = () => {
    dispatchEmail({
      type: 'INPUT_BLUR'
    });
  };

  const validatePasswordHandler = () => {
    dispatchPassword({
      type: 'INPUT_BLUR'
    });
  };

  const submitHandler = (event) => {
    event.preventDefault();
    authctx.onLogin(emailState.value, passwordState.value);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${emailState.isValid === false ? classes.invalid : ''
            }`}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={emailState.value}
            onChange={emailChangeHandler}
            onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${passwordState.isValid === false ? classes.invalid : ''
            }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={passwordState.value}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
