import Button from './Button';
import ReactDOM from 'react';
import style from './Modal.module.css';

export default function Alert(props) {

  const Backdrop = (props) => {
    return (
      <div className={`${style.modal} ${showModal}`} role="dialog" onClick={props.closeModal}></div>
    );
  }

  const ModalOVerlay = (props) => {
    return (
      <div className={`${style["modal-dialog"]} ${animationClose}`}>
        <div className={style["modal-content"]}>
          <div className={style["modal-header"]}>
            <h5 className={style["modal-title"]}>{props.messageTitle}</h5>
          </div>
          <div className={style["modal-body"]}>
            {props.messageBody}
          </div>
          <div className={style["modal-footer"]}>
            <Button type="button" className={style["btn-primary"]} Message={"Aceptar"} onClick={props.closeModal} />
          </div>
        </div>
      </div>
    );
  }

  let showModal, animationClose;
  if (props.closeModal != null) {
    showModal = `${style.show} ${style.fade}`;
  } else {
    animationClose = `${style["modal-animation-close"]}`;
  }

  let BackDropElement = document.getElementById('backdrop-root'),
    ModalOverlayElement = document.getElementById('overlay-root');

  return (
    <>
      {ReactDOM.createPortal(<Backdrop closeModal={props.closeModal} />, BackDropElement)}
    </>
  );
}
