import React, { useState } from "react";
import Card from "../../IU/Card";
import style from "./UserInfo.module.css";

export default function UserInfo(props) {


  const [userData, setUserData] = useState({
    userName: '',
    userAge: ''
  });

  // * We validate the data entered
  const [isValid, setIsValid] = useState(true);

  // * Functions for data capture and shipping
  const NameChangeHandler = evt => {
    setUserData(prevData => {
      return {
        ...prevData,
        userData: evt.target.value
      };
    });
  };

  const AgeChangeHandler = (evt) => {
    setUserData(prevData => {
      return {
        ...prevData,
        userAge: evt.target.value
      };
    });
    setIsValid(true);
  };

  const submitHandler = (evt) => {
    evt.preventDefault();

    const UserInfo = {
      name: userData.userName,
      age: userData.userAge
    };

    if (userData.userAge === '' || userData.userAge < 1) {
      setIsValid(false);
      return;
    }
    // * Once the data is sent, we empty the fields
    setUserData({
      userName: '',
      userAge: ''
    });
  };

  return (
    <Card>
      <form onSubmit={submitHandler}>
        <div className={style.add__newUser}>
          <div className={style.add__newUser_inputContainer}>
            <label className={style.add__newUser_formLabel}>Nombre completo:</label>
            <input value={userData.userName} onChange={NameChangeHandler} className={style.add__newUser_formControl} type="text" placeholder="Panfilo Gonzaléz Ramiréz"></input>
          </div>
          <div className={style.add__newUser_inputContainer}>
            <label className={style.add__newUser_formLabel}>Edad:</label>
            <input value={userData.userAge} onChange={AgeChangeHandler} className={`${style.add__newUser_formControl}, ${!isValid && 'invalid'}`} type="number" min="1"></input>
            <div className={style.add_newUser_formText}>Ingresa una edad correcta</div>
          </div>
        </div>
        <div className={style.add__newUser_actions}>
          <button type="submit" className={style.add__newUser_success}>Agregar usuario</button>
        </div>
      </form>
    </Card>
  );
}
