import React from "react";
import Card from "../IU/Card";
import Modal from "../UI/Modal";
import InfoRow from "../IU/InfoRow";

export default function UserList(props) {

  let userCard;
  if (props.data.length === 0) {
    let messagecontent = <p>Disculpe las molestias, pero es de suma importancia que ingrese todos sus datos</p>;
    return <Modal messageTitle="Datos incorrectos" messageBody={messagecontent} />;
  } else {
    userCard = props.data.map((info) => <InfoRow key={info.key} message={`${info.name} (${info.age} años)`} />)
  }
  /* props.data.map((info) => {
          return console.log(`nombre: ${info.name} Edad: ${info.age}`)
        }) */
  return (
    <Card>
      {userCard}
    </Card>
  );
}
