import React, { useState } from "react";
import Card from "../../IU/Card";
import style from "./UserInfo.module.css";

export default function UserInfo(props) {


  const [userData, setUserData] = useState({
    userName: '',
    userAge: ''
  });

  // * We validate that age is correct
  const [isValid, setIsValid] = useState({
    validName: true,
    validAge: true
  });

  // * Functions for data capture and shipping
  const NameChangeHandler = evt => {
    setUserData(prevData => {
      return {
        ...prevData,
        userName: evt.target.value
      };
    });
  };

  const AgeChangeHandler = (evt) => {
    setUserData(prevData => {
      return {
        ...prevData,
        userAge: evt.target.value
      };
    });
    setIsValid(prevData => {
      return {
        ...prevData,
        validAge: true
      };
    });
  };

  const submitHandler = (evt) => {
    evt.preventDefault();

    const UserInfo = {
      key: Math.random(),
      name: userData.userName,
      age: userData.userAge
    };

    // * Validation of the data entered
    // eslint-disable-next-line default-case
    switch (true) {
      case (UserInfo.name === ''):
        setIsValid(prevData => {
          return {
            ...prevData,
            validName: false
          }
        });
        break;
      case (UserInfo.age === '' || UserInfo.age < 1):
        setIsValid(prevData => {
          return {
            ...prevData,
            validAge: false
          }
        });
        break;
    }

    if (!isValid.validAge) {
      alert("ingresa tu edad");
    }

    // * Once the data is sent, we empty the fields
    setUserData({
      userName: '',
      userAge: ''
    });
  };

  return (
    <Card>
      <form onSubmit={submitHandler}>
        <div className={style.add__newUser}>
          <div className={style.add__newUser_inputContainer}>
            <label className={style.add__newUser_formLabel}>Nombre completo:</label>
            <input type="text" value={userData.userName} onChange={NameChangeHandler} className={style.add__newUser_formControl} />
          </div>
          <div className={style.add__newUser_inputContainer}>
            <label className={style.add__newUser_formLabel}>Edad:</label>
            <input value={userData.userAge} onChange={AgeChangeHandler} className={`${style.add__newUser_formControl} ${!isValid.validAge && style.invalid}`} type="number" min="1" />
            {!isValid.validAge && <div className={style.add_newUser_formText}>Ingresa una edad correcta</div>}
          </div>
        </div>
        <div className={style.add__newUser_actions}>
          <button type="submit" className={style.add__newUser_success}>Agregar usuario</button>
        </div>
      </form>
    </Card>
  );
}
